// SPDX-License-Identifier: Apache-2.0
pragma solidity >=0.8.24 <0.9.0;

/// @title StorkErrors
/// @notice Error definitions for the Stork protocol
library FirstPartyStorkErrors {
    /// @notice Insufficient fee is paid to the method
    /// @dev Error code: 0x025dbdd4
    error InsufficientFee();

    /// @notice There is no fresh update, whereas expected fresh updates
    /// @dev Error code: 0xde2c57fa
    error NoFreshUpdate();

    /// @notice Not found
    /// @dev Error code: 0xc5723b51
    error NotFound();

    /// @notice Signature is invalid
    /// @dev Error code: 0x8baa579f
    error InvalidSignature();
}

project/contracts/FirstPartyStorkEvents..content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity >=0.8.24 <0.9.0;

/// @title IFirstPartyStorkEvents
/// @notice Events emitted by the FirstPartyStork contract
/// @dev This interface can be used for listening to updates for off-chain and testing purposes
interface IFirstPartyStorkEvents {
    /// @notice Emitted when the latest value with `assetId` has received a fresh update
    /// @param pubKey The publisher's public key
    /// @param assetId The asset ID
    /// @param timestampNs Publish time of the given update
    /// @param quantizedValue Value of the given update
    event ValueUpdate(
        address indexed pubKey,
        string indexed assetId,
        uint64 timestampNs,
        int192 quantizedValue
    );

    event HistoricalValueStored(
        address indexed pubKey,
        string indexed assetId,
        uint64 timestampNs,
        int192 quantizedValue,
        uint256 roundId
    );

    event PublisherUserAdded(address indexed pubKey, uint256 singleUpdateFee);

    event PublisherUserRemoved(address indexed pubKey);
}

project/contracts/FirstPartyStorkGetters.content:
// contracts/stork/StorkGetters.sol
// SPDX-License-Identifier: Apache 2

pragma solidity >=0.8.24 <0.9.0;

import "./FirstPartyStorkStructs.sol";
import "./FirstPartyStorkState.sol";
import "./FirstPartyStorkErrors.sol";

contract FirstPartyStorkGetters is FirstPartyStorkState {
    function getLatestTemporalNumericValue(
        address pubKey,
        string memory assetPairId
    ) public view returns (FirstPartyStorkStructs.TemporalNumericValue memory value) {
        if (_state.latestValues[pubKey][assetPairId].timestampNs == 0) {
            revert FirstPartyStorkErrors.NotFound();
        }

        return _state.latestValues[pubKey][assetPairId];
    }

    function getHistoricalTemporalNumericValue(
        address pubKey,
        string memory assetPairId,
        uint256 roundId
    ) public view returns (FirstPartyStorkStructs.TemporalNumericValue memory) {
        if (roundId >= _state.historicalValues[pubKey][assetPairId].length) {
            revert FirstPartyStorkErrors.NotFound();
        }

        return _state.historicalValues[pubKey][assetPairId][roundId];
    }

    function getHistoricalRecordsCount(
        address pubKey,
        string memory assetPairId
    ) public view returns (uint256) {
        return _state.historicalValues[pubKey][assetPairId].length;
    }

    function getCurrentRoundId(
        address pubKey,
        string memory assetPairId
    ) public view returns (uint256) {
        return _state.currentRoundId[pubKey][assetPairId];
    }

    function getPublisherUser(
        address pubKey
    ) public view returns (FirstPartyStorkStructs.PublisherUser memory) {
        if (_state.publisherUsers[pubKey].pubKey == address(0)) {
            revert FirstPartyStorkErrors.NotFound();
        }

        return _state.publisherUsers[pubKey];
    }
}

project/contracts/FirstPartyStorkSetters.content:
// contracts/stork/StorkSetters.sol
// SPDX-License-Identifier: Apache 2

pragma solidity >=0.8.24 <0.9.0;

import "./FirstPartyStorkEvents.sol";
import "./FirstPartyStorkStructs.sol";
import "./FirstPartyStorkState.sol";

contract FirstPartyStorkSetters is FirstPartyStorkState, IFirstPartyStorkEvents {
    function updateLatestValueIfNecessary(
        address pubKey,
        FirstPartyStorkStructs.PublisherTemporalNumericValueInput memory input
    ) internal returns (bool) {
        uint64 latestReceiveTime = _state
        .latestValues[pubKey][input.assetPairId].timestampNs;
        if (input.temporalNumericValue.timestampNs < latestReceiveTime) {
            return false;
        }

        _state.latestValues[pubKey][input.assetPairId] = input.temporalNumericValue;
        emit ValueUpdate(
            pubKey,
            input.assetPairId,
            input.temporalNumericValue.timestampNs,
            input.temporalNumericValue.quantizedValue
        );
        return true;
    }

    function storeHistoricalValue(
        address pubKey,
        FirstPartyStorkStructs.PublisherTemporalNumericValueInput memory input
    ) internal returns (bool) {
        uint64 latestReceiveTime = _state
        .latestValues[pubKey][input.assetPairId].timestampNs;
        if (input.temporalNumericValue.timestampNs < latestReceiveTime) {
            return false;
        }

        _state.historicalValues[pubKey][input.assetPairId].push(
            FirstPartyStorkStructs.TemporalNumericValue(
                input.temporalNumericValue.timestampNs,
                input.temporalNumericValue.quantizedValue
            )
        );
        _state.currentRoundId[pubKey][input.assetPairId]++;
        emit HistoricalValueStored(
            pubKey,
            input.assetPairId,
            input.temporalNumericValue.timestampNs,
            input.temporalNumericValue.quantizedValue,
            _state.currentRoundId[pubKey][input.assetPairId]
        );
        return true;
    }

    function addPublisherUser(
        address pubKey,
        uint256 singleUpdateFee
    ) internal {
        _state.publisherUsers[pubKey] = FirstPartyStorkStructs.PublisherUser(
            pubKey,
            singleUpdateFee
        );
        emit PublisherUserAdded(pubKey, singleUpdateFee);
    }

    function removePublisherUser(address pubKey) internal {
        delete _state.publisherUsers[pubKey];
        emit PublisherUserRemoved(pubKey);
    }
}

project/contracts/FirstPartyStorkState.s.content:
// contracts/stork/StorkState.sol
// SPDX-License-Identifier: Apache 2

pragma solidity >=0.8.24 <0.9.0;

import "./FirstPartyStorkStructs.sol";

contract FirstPartyStorkStorage {
    struct State {
        mapping(address => mapping(string => FirstPartyStorkStructs.TemporalNumericValue)) latestValues;
        mapping(address => mapping(string => FirstPartyStorkStructs.TemporalNumericValue[])) historicalValues;
        mapping(address => mapping(string => uint256)) currentRoundId;
        mapping(address => FirstPartyStorkStructs.PublisherUser) publisherUsers;
    }
}

contract FirstPartyStorkState {
    FirstPartyStorkStorage.State _state;
}

project/contracts/FirstPartyStorkStructs.content:
// SPDX-License-Identifier: Apache-2.0
pragma solidity >=0.8.24 <0.9.0;

/// @title StorkStructs
/// @notice Data structures used by the Stork protocol
library FirstPartyStorkStructs {
    /// @notice Represents a temporal numeric value with timestamp and quantized value
    struct TemporalNumericValue {
        /// @dev Nanosecond level precision timestamp of latest publisher update in batch
        uint64 timestampNs; // 8 bytes
        /// @dev Should be able to hold all necessary numbers (up to 6277101735386680763835789423207666416102355444464034512895)
        int192 quantizedValue; // 24 bytes
    }

    /// @notice Input structure for updating temporal numeric values
    struct PublisherTemporalNumericValueInput {
        TemporalNumericValue temporalNumericValue;
        address pubKey;
        string assetPairId;
        bytes32 r;
        bytes32 s;
        uint8 v;
    }

    struct PublisherUser {
        address pubKey;
        uint256 singleUpdateFee;
    }
}

